package LEC1;

public class Max_Of_Three_Num {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	int a=4;
	int b=9;
	int c=10;
	
	if(a>=b && a>=c) {
		System.out.println(a+"is greater");
	}else if(b>=a&& b>=c){
		System.out.println(b+"is greater");
	}else {
		System.out.println(c+"is greater");
	}

	}

}
