package Lecture6;

import java.util.Scanner;

public class Shopping_game {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		int a=sc.nextInt();
		for(int i=0;i<a;i++) {
			int ayus=sc.nextInt();
			int her=sc.nextInt();
			if(ayus>=her) {
				System.out.println("Aayush");
			}else {
				System.out.println("Harshit");	
			}
		}

	}

}
